function loadData(a,t){addLineChartData(a),addSidoBarChartData(t)}function addLineChartData(a){$.getJSON("data/dateData.json",function(t){var d=0;$.each(t,function(t,n){const e=String(n.date).substring(5,10),s=Number(n.infected),o=s-d;d=s,a.data.labels.push(e),a.data.datasets[1].data.push(s),a.data.datasets[0].data.push(o)}),a.update()})}function addSidoBarChartData(a){$.getJSON("data/todaySidoData.json",function(t){$.each(t,function(t,d){const n=d.state;var e=Number(d.infected);const s=a.data.labels.indexOf(n);e+=null==a.data.datasets[0].data[s]?0:a.data.datasets[0].data[s],a.data.datasets[0].data[s]=e}),a.update()}).done(function(a){sidoData=a})}